import React, {useState} from 'react' // useText is a hook 

export default function TextForm(props) {
    const handleUpClick = () =>{
        console.log("upper case was clicked.");
        let newText = text.toUpperCase();
        setText("yoou have clicked handleupclick");
    }
    const handleOnChange = (event) =>{
        console.log("On change");
        setText(event.target.value);
    }

    const [text, setText] = useState('Enter your text'); // text here is a state variable that that uses useState hook
    return (
        <div>
            <h1>{props.heading}</h1>
            <div className="mb-3">
                <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="10"></textarea>
            </div>
            <button className= "btn btn-primary" onClick={handleUpClick}>Change to Uppercase</button>
        </div>
    )
}
