import React, {useState} from 'react' // useText is a hook 

export default function TextForm(props) {
    const [text, setText] = useState('Enter your text'); // text here is a state variable that
    return (
        <div>
            <h1>{props.heading} - {text}</h1>
            <div className="mb-3">
                <textarea className="form-control" id="myBox" rows="10"></textarea>
            </div>
            <button className= "btn btn-primary">Change to Uppercase</button>
        </div>
    )
}
